import React, { useState, useEffect } from "react";

function App() {
  // Load habits from Local Storage
  const [habits, setHabits] = useState(() => {
    try {
      const savedHabits = localStorage.getItem("habits");
      return savedHabits ? JSON.parse(savedHabits) : [];
    } catch (error) {
      console.error("Error loading habits:", error);
      return [];
    }
  });

  // Save habits to Local Storage
  useEffect(() => {
    try {
      localStorage.setItem("habits", JSON.stringify(habits));
    } catch (error) {
      console.error("Error saving habits:", error);
    }
  }, [habits]);

  const [newHabit, setNewHabit] = useState("");

  // ‚úÖ Add a habit with streak tracking
  const addHabit = () => {
    if (newHabit.trim() !== "") {
      const updatedHabits = [
        ...habits,
        { text: newHabit, completed: false, streak: 0 },
      ];
      setHabits(updatedHabits);
      setNewHabit("");
    }
  };

  // ‚úÖ Toggle completion & increase streak
  const toggleCompletion = (index) => {
    const updatedHabits = habits.map((habit, i) => {
      if (i === index) {
        return {
          ...habit,
          completed: !habit.completed,
          streak: habit.completed ? habit.streak : habit.streak + 1,
        };
      }
      return habit;
    });
    setHabits(updatedHabits);
  };

  // ‚úÖ Delete a habit
  const deleteHabit = (index) => {
    const updatedHabits = habits.filter((_, i) => i !== index);
    setHabits(updatedHabits);
  };

  // ‚úÖ Calculate progress
  const completedHabits = habits.filter(habit => habit.completed).length;
  const totalHabits = habits.length;
  const progressPercentage = totalHabits > 0 ? (completedHabits / totalHabits) * 100 : 0;

  return (
    <div className="container">
      <h1 className="title">Habit Tracker</h1>

      {/* ‚úÖ Progress Bar */}
      <div className="progress-container">
        <div className="progress-bar" style={{ width: `${progressPercentage}%` }}>
          {Math.round(progressPercentage)}%
        </div>
      </div>

      <div className="input-container">
        <input
          type="text"
          placeholder="Add a new habit"
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
          className="input-box"
        />
        <button onClick={addHabit} className="add-btn">Add</button>
      </div>

      <ul className="habit-list">
        {habits.map((habit, index) => (
          <li key={index} className="habit-item">
            <input
              type="checkbox"
              checked={habit.completed}
              onChange={() => toggleCompletion(index)}
              className="checkbox"
            />
            <span className={habit.completed ? "completed" : ""}>{habit.text}</span>
            <span className="streak">üî• {habit.streak} days</span>
            <button onClick={() => deleteHabit(index)} className="delete-btn">‚ùå</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
