import React, { useState, useEffect } from 'react';
function App() {
    const [habits, setHabits] = useState(() => {
    const savedHabits = localStorage.getItem("habits");
    return savedHabits ? JSON.parse(savedHabits) : [];
  });

  useEffect(() => {
    localStorage.setItem("habits", JSON.stringify(habits));
  }, [habits]);
  const [newHabit, setNewHabit] = useState('');

  const addHabit = () => {
    if (newHabit.trim() !== '') {
      setHabits([...habits, { text: newHabit, completed: false }]);
      setNewHabit('');
    }
  };

  const toggleCompletion = (index) => {
    const updatedHabits = habits.map((habit, i) =>
      i === index ? { ...habit, completed: !habit.completed, streak: (habit.streak || 0) + 1 } : habit
    );
    setHabits(updatedHabits);
  };

  const deleteHabit = (index) => {
    setHabits(habits.filter((_, i) => i !== index));
  };

  return (
    <div className="p-4 max-w-lg mx-auto">
      <h1 className="text-2xl font-bold mb-4">Habit Tracker</h1>
      <div className="flex gap-2 mb-4">
        <input
          type="text"
          placeholder="Add a new habit"
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
          className="border p-2 flex-grow"
        />
        <button onClick={addHabit} className="bg-blue-500 text-white p-2">Add</button>
      </div>
      <ul>
        {habits.map((habit, index) => (
          <li key={index} className="flex justify-between p-2 border-b">
            <span
              className={`cursor-pointer ${habit.completed ? 'line-through text-gray-500' : ''}`}
              onClick={() => toggleCompletion(index)}
            >
              {habit.text}
            </span>
            <button onClick={() => deleteHabit(index)} className="delete-btn">‚ùå</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
