import React, { useState, useEffect } from "react";

function App() {
  // ‚úÖ Load habits from Local Storage
  const [habits, setHabits] = useState(() => {
    try {
      const savedHabits = localStorage.getItem("habits");
      return savedHabits ? JSON.parse(savedHabits) : [];
    } catch (error) {
      console.error("Error loading habits:", error);
      return [];
    }
  });

  const [newHabit, setNewHabit] = useState("");
  const [darkMode, setDarkMode] = useState(() => {
    return localStorage.getItem("darkMode") === "true";
  });

  // ‚úÖ Save habits to Local Storage
  useEffect(() => {
    try {
      localStorage.setItem("habits", JSON.stringify(habits));
    } catch (error) {
      console.error("Error saving habits:", error);
    }
  }, [habits]);

  // ‚úÖ Save dark mode preference
  useEffect(() => {
    localStorage.setItem("darkMode", darkMode);
  }, [darkMode]);

  // ‚úÖ Add Habit
  const addHabit = () => {
    if (newHabit.trim() !== "") {
      setHabits([...habits, { text: newHabit, completed: false, streak: 0 }]);
      setNewHabit(""); // Clear input after adding
    }
  };

  // ‚úÖ Toggle Completion
  const toggleCompletion = (index) => {
    const updatedHabits = habits.map((habit, i) => {
      if (i === index) {
        return { 
          ...habit, 
          completed: !habit.completed,
          streak: habit.completed ? 0 : habit.streak + 1 // Reset streak if unchecked
        };
      }
      return habit;
    });
    setHabits(updatedHabits);
  };

  // ‚úÖ Delete Habit
  const deleteHabit = (index) => {
    const updatedHabits = habits.filter((_, i) => i !== index);
    setHabits(updatedHabits);
  };

  // ‚úÖ Calculate Progress
  const completedHabits = habits.filter(habit => habit.completed).length;
  const totalHabits = habits.length;
  const progressPercentage = totalHabits > 0 ? (completedHabits / totalHabits) * 100 : 0;

  // ‚úÖ Export Data
  const exportData = () => {
    const dataStr = "data:text/json;charset=utf-8," + encodeURIComponent(JSON.stringify(habits));
    const downloadAnchor = document.createElement("a");
    downloadAnchor.setAttribute("href", dataStr);
    downloadAnchor.setAttribute("download", "habits.json");
    document.body.appendChild(downloadAnchor);
    downloadAnchor.click();
    document.body.removeChild(downloadAnchor);
  };

  // ‚úÖ Import Data
  const importData = (event) => {
    const file = event.target.files[0];
    if (!file) return;
    
    const reader = new FileReader();
    reader.onload = (e) => {
      try {
        const importedHabits = JSON.parse(e.target.result);
        setHabits(importedHabits);
      } catch (error) {
        console.error("Invalid file format", error);
      }
    };
    reader.readAsText(file);
  };

  return (
    <div className={`container ${darkMode ? "dark-mode" : ""}`}>
      <h1 className="title">Habit Tracker</h1>

      {/* ‚úÖ Dark Mode Toggle */}
      <button onClick={() => setDarkMode(!darkMode)} className="dark-mode-btn">
        {darkMode ? "‚òÄÔ∏è Light Mode" : "üåô Dark Mode"}
      </button>

      {/* ‚úÖ Progress Bar */}
      <div className="progress-container">
        <div className="progress-bar" style={{ width: `${progressPercentage}%` }}>
          {Math.round(progressPercentage)}%
        </div>
      </div>

      <div className="input-container">
        <input
          type="text"
          placeholder="Add a new habit"
          value={newHabit}
          onChange={(e) => setNewHabit(e.target.value)}
          className="input-box"
        />
        <button onClick={addHabit} className="add-btn">Add</button>
      </div>

      <ul className="habit-list">
        {habits.map((habit, index) => (
          <li key={index} className="habit-item">
            <input
              type="checkbox"
              checked={habit.completed}
              onChange={() => toggleCompletion(index)}
              className="checkbox"
            />
            <span className={habit.completed ? "completed" : ""}>{habit.text}</span>
            <span className="streak">üî• {habit.streak} days</span>
            <button onClick={() => deleteHabit(index)} className="delete-btn">‚ùå</button>
          </li>
        ))}
      </ul>

      {/* ‚úÖ Export & Import */}
      <button onClick={exportData} className="export-btn">üìÅ Export Habits</button>
      <input type="file" onChange={importData} className="import-btn" />
    </div>
  );
}

export default App;
